            text += "  pem_ca_cert_file : " + ( !updateReqXml.caCert.pem_ca_cert_file ? "null" : updateReqXml.caCert.pem_ca_cert_file ) + "\n";
            text += "  pem_crl_file : " + ( !updateReqXml.caCert.pem_crl_file ? "null" : updateReqXml.caCert.pem_crl_file ) + "\n";
